'use strict';
({params, imports}) => {
  const programEnrolment = params.entity;
  const formElement = params.formElement;
  const statusBuilder = new imports.rulesConfig.FormElementStatusBuilder({programEnrolment, formElement});
  statusBuilder.show().when.valueInEnrolment('Were you able to collect and verify all documents').containsAnswerConceptName("No");
  return statusBuilder.build();
};

