decisionRule:
"use strict";
({params, imports}) => {
    const decisions = params.decisions;
    const programEncounter = params.entity;
    const schemeDetails = programEncounter.name.split('-');
    const schemeName = imports.lodash.trim(schemeDetails[0]);
    const workflow = imports.lodash.trim(schemeDetails[1]);
    const workflowName = workflow === 'Update' ? 'Update' : (workflow === 'Recurring' ? 'Register' : 'Enrolment');
    decisions.encounterDecisions.push({name: 'Scheme', value: [schemeName]});
    decisions.encounterDecisions.push({name: 'Scheme status', value: ['Facilitation in progress']});
    decisions.encounterDecisions.push({name: 'Facilitation workflow', value: [workflowName]});
    decisions.encounterDecisions.push({name: 'Facilitation status', value:  ['Submit application']});
    return decisions;
};

visitScheduleRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
        programEncounter,
        programEnrolment: programEncounter.programEnrolment
    });
    const schemeDetails = programEncounter.name.split('-');
    const workflow = imports.lodash.trim(schemeDetails[1]);
    const schemeName = imports.lodash.trim(schemeDetails[0]);
    const maxDate = imports.moment(programEncounter.encounterDateTime).add(1, 'days').toDate();
    if (imports.moment().isSameOrBefore(maxDate, 'day')) {
        scheduleBuilder.add({
            name: workflow === 'Update' ? `${schemeName} - Update - Submit Application` : `${schemeName} - Submit Application`,
            encounterType: 'Submit Application',
            earliestDate: programEncounter.encounterDateTime,
            maxDate
        });
    }
    return scheduleBuilder.getAllUnique('name');
};
