visitScheduleRule:
"use strict";
({params, imports}) => {
    const schemeDetails = new Map();
    const programEncounter = params.entity;
    const observation = programEncounter.findObservation('Register scheme');
    const conceptAnswers = !_.isEmpty(observation) && observation.concept.getAnswers();
    _.forEach(conceptAnswers, ca => {
        const keyValues = _.map(ca.concept.keyValues, ({key, value}) => ({[key]: ca.concept.recordValueByKey(key)}));
        schemeDetails.set(ca.concept.name, Object.assign({}, ...keyValues))
    });
    const schemesToRegister = programEncounter.getObservationReadableValue("Register scheme");
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
        programEncounter,
        programEnrolment: programEncounter.programEnrolment
    });
    const earliestDate = programEncounter.encounterDateTime;
    const maxDate = imports.moment(earliestDate).add(8, 'days').toDate();
    if (schemeDetails.get(schemesToRegister).recurring && imports.moment().isSameOrBefore(maxDate, 'day')) {
        scheduleBuilder.add({
            name: "Start Facilitation For Recurring Scheme",
            encounterType: "Start Facilitation",
            earliestDate: earliestDate,
            maxDate: maxDate
        });
    }
    return scheduleBuilder.getAllUnique('name');
};
