visitScheduleRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
        programEncounter,
        programEnrolment: programEncounter.programEnrolment
    });
    const schemeName = imports.lodash.trim(programEncounter.name.split('-')[0]);
    const scheduleNextStep = programEncounter.getObservationReadableValue("Has the application been submitted");
    if (scheduleNextStep === 'Yes') {
        scheduleBuilder.add({
            name: `${schemeName} - Application Status Enquiry`,
            encounterType: 'Enquire Application Status',
            earliestDate: programEncounter.encounterDateTime,
            maxDate: imports.moment(programEncounter.encounterDateTime).add(7, 'days').toDate()
        });
    }
    return scheduleBuilder.getAllUnique('name');
};
