visitScheduleRule:
"use strict";
({ params, imports}) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter,
    programEnrolment: programEncounter.programEnrolment
  });
  const earliestDate = programEncounter.encounterDateTime;
  const numberOfDaysForMaxOffset = 7;
  const maxDate = imports.moment(earliestDate).add(numberOfDaysForMaxOffset, 'days').toDate();
  const encounterType = 'Enquire Application Status';
  const schemeName = imports.lodash.trim(programEncounter.name.split('-')[0]);
  scheduleBuilder.add({
    //TODO : change name to <Scheme name> - <Workflow name> - Collect Document
    name: `${schemeName} - Application Status Enquiry`,
    encounterType,
    earliestDate,
    maxDate
  });
  return scheduleBuilder.getAllUnique('name');
};
